import React, { useState } from 'react';
import './App.css';
import Content from './Content';
import Header from './Header';
import Footer from './Footer';

function App() {
  // State pour la couleur de fond de la page
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF');

  // Fonction pour changer la couleur de fond de la page
  const changeBackgroundColor = () => {
    setBackgroundColor('#ffcc00'); // Changez la couleur de fond ici selon votre besoin
  };

  return (
    <div className='App' style={{ backgroundColor: backgroundColor }}>
      <Header />
      <Content />
      <Footer />
      <button onClick={changeBackgroundColor}>Changer le fond</button>
    </div>
  );
}

export default App;
