import React from 'react';
import './App.css';
import Content from './Content';
import Header from './Header';
import Footer from './Footer';

function App() {
  const [showContent, setShowContent] = React.useState(false);

  const handleEnter = () => {
    setShowContent(true);
  };

  return (
    <div className="App">
      <Header onEnter={handleEnter} />
      {showContent && (
        <>
          <Content />
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
